import * as THREE from 'three'
import React, { useState, useRef } from 'react'
import { useLoader, useFrame } from 'react-three-fiber'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'

const geometry = new THREE.BoxBufferGeometry(1, 1, 40)
const position = new THREE.Vector3()
const direction = new THREE.Vector3()

export default function Ship() {
  const gltf = useLoader(GLTFLoader, '/carrito.gltf')
  const main = useRef()

  const glassMaterialDefault = new THREE.MeshPhysicalMaterial({
    color: 0xaaaaee,
    roughness: 0.0
  })

  const wheelsMaterialDefault = new THREE.MeshPhysicalMaterial({
    color: 0x555500,
    roughness: 0.9
  })

  const materialDefault = new THREE.MeshPhysicalMaterial({
    color: 0x00cc00,
    roughness: 0.8
  })
  const materialHover = new THREE.MeshPhysicalMaterial({
    color: 0xcc0000,
    roughness: 0.1
  })

  const [hovered, setHover] = useState(false)
  const hover = e => e.stopPropagation() && setHover(true)
  const unhover = e => e.stopPropagation() && setHover(false)

  useFrame(() => {
    // Get ships orientation and save it to the stores ray
    main.current.getWorldPosition(position)
    main.current.getWorldDirection(direction)

  })

  function CarContent() {
    // Useref permet de ne pas avoir de dbl alert qd on clique
    const ref = useRef()
    // useFrame(() => (ref.current.rotation.x = ref.current.rotation.y = ref.current.rotation.z += 0.01))
    return (
      <group ref={ref}>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[10].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[11].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[13].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          // material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[3].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          // material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[3].geometry} />
        </mesh>
      </group>
    )
  }

  return (
    <group ref={main}>
      <group scale={[1, 1, 1]}>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[10].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[11].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[13].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          // material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[3].geometry} />
        </mesh>
        <mesh 
          onPointerOver={hover}
          onPointerOut={unhover}
          // material={hovered ? materialHover : materialDefault}
          position={[0, 0, 0]}>
            <bufferGeometry attach="geometry" {...gltf.__$[3].geometry} />
        </mesh>
      </group>
    </group>
  )
}


